css:
a, b, body, em, h1, h2, h3, h4, h5, h6, li, small, span, strong, ul {
    font-family: revert !important;
    font-style: normal;
    -webkit-font-smoothing: antialiased;
    -webkit-text-stroke-width: .2px;
    -moz-osx-font-smoothing: grayscale;
}
.product__free-shipping-from{
  display:none !important;
}
.header__nav a {
    text-transform: uppercase;
    font-weight: var(--font-headings);
    padding: 0 var(--spacer-1);
    border-radius: var(--border-radius-sm);
    display: inline-block;
    transition: background-color .15s;
    padding: 10px;
    font-size: 13px;
}
.header__nav a:hover {
    /* background: var(--primary-lightest); */
    text-decoration: revert;
    background: transparent !important;
}
#cart-count{
  background: red !important;
  color: white !important;
}
#cart-quickview > aside > footer > a.minicart__btn-checkout.btn.btn-block.btn-primary{
  background: green;
    border-radius: 0px !important;
    font-size: 22px;
  text-transform: uppercase;
}
.login__oauth{
  display: none !important;
}
#checkout > div > div > div > span > div > div > form > div.login__email.input-group.input-group-lg > div > button{
    background: green;
    border-radius: 0px !important;
    font-size: 19px;
  text-transform: uppercase;
}
#product-actions > div > div.product__buy > span > button
{
  background: #117878;
    border-radius: 0px;
    border: none;
    width: 100%;
    text-transform: uppercase;
    font-size: 26px;
}
.product__name{
  font-size: 16px;
    text-transform: uppercase;
    font-weight: lighter;
}
.product__short-stock{
  display: none !important;
}
.prices--big .prices__value {
    font-size: 25px !important;
    color: #117878 !important;
}
.prices__compare{
  color: red !important;
}
.prices__installments{
  font-size: 12px !important;
}
